mapper.update:
UPDATE `user` SET
  password = #{password},
  id_card = #{idCard}
WHERE username = #{username}

UPDATE user_info SET
  `name` = #{name},
  sex = #{sex},
  phone = #{phone},
  email = #{email}
WHERE user_info.`user` = #{user.username}

UPDATE `user` SET
  password = #{password},
  id_card = #{idCard},
  first_login = ${firstLogin}
WHERE username = #{username}

UPDATE `data` SET
  flag = 1,
  delete_time = #{deleteTime}
WHERE id IN ${ids}

mapper.select:
SELECT system_notice.id, system_notice.`name`, content, `date`, user1.username as username, user1.password, user1.id_card as idCard, user1.first_login as firstLogin, role.id as roleId, role.name as roleName FROM system_notice LEFT JOIN `user` as user1 ON user1.username = system_notice.user LEFT JOIN role ON role.id = system_notice.role WHERE 1=1
AND (SELECT department.code FROM user_info LEFT JOIN department ON department.code = user_info.department LEFT JOIN `user` as user2 ON user_info.user = user2.username WHERE user2.username = user1.username) = #{departmentCode}
AND role.id = #{roleId}
AND system_notice.id = #{noticeId}
ORDER BY system_notice.`date` DESC

SELECT data_class.id, data_class.`name`, role.id as roleId, role.name as roleName, `user`.username, `user`.password, `user`.id_card as idCard, `user`.first_login as firstLogin, department.code as departmentCode, department.name as departmentName, class_types as classTypes, data_class.flag FROM data_class LEFT JOIN role ON data_class.role = role.id LEFT JOIN `user` ON data_class.`user` = `user`.username LEFT JOIN department ON data_class.department = department.code WHERE department.code = #{departmentCode}
AND data_class.flag in ${flag}
AND role.id = #{roleId}
AND (SELECT role.name FROM user_role LEFT JOIN role ON user_role.role = role.id WHERE user_role.user=data_class.user) IN ${userType}

SELECT data_class.id, data_class.`name`, role.id as roleId, role.name as roleName, `user`.username, `user`.password, `user`.id_card as idCard, `user`.first_login as firstLogin, department.code as departmentCode, department.name as departmentName, class_types as classTypes, data_class.flag FROM data_class LEFT JOIN role ON data_class.role = role.id LEFT JOIN `user` ON data_class.`user` = `user`.username LEFT JOIN department ON data_class.department = department.code WHERE department.code = #{departmentCode}
AND data_class.flag in ${flag}
AND role.id = #{roleId}
AND (SELECT role.name FROM user_role LEFT JOIN role ON user_role.role = role.id WHERE user_role.user=data_class.user) IN ${userType}

SELECT `data`.id, content, file_name as fileName, src, data_class.id as dataClassId, data_class.name as dataClassName, role.id as dataClassRoleId, role.name as dataClassRoleName, user1.username as dataClassUserUsername, user1.password as dataClassUserPassword, user1.id_card as dataClassUserIdCard, user1.first_login as dataClassUserFirstLogin, department.code as dataClassDepartmentCode, department.name as dataClassDepartmentName, data_class.flag as dataClassFlag, type_contents as typeContents, user2.username, user2.password, user2.id_card as idCard, user2.first_login as firstLogin, submit_time as submitTime, delete_time as deleteTime, `data`.flag FROM `data` LEFT JOIN data_class ON `data`.data_class = data_class.id LEFT JOIN role ON data_class.role = role.id LEFT JOIN `user` user1 ON data_class.`user` = user1.username LEFT JOIN department ON data_class.department = department.code LEFT JOIN `user` user2 ON `data`.user = user2.username WHERE 1=1
AND user2.username = #{username}
AND data_class.id = #{dataClassId}
AND `data`.flag = ${dataFlag}
AND data_class.flag = ${dataClassFlag}
AND `data`.id = #{dataId}
AND SUBSTRING_INDEX(`data`.type_contents,'/',1) = #{typeContent1}
AND SUBSTRING_INDEX(SUBSTRING_INDEX(`data`.type_contents,'/',2),'/',-1) = #{typeContent2}
AND SUBSTRING_INDEX(`data`.type_contents,'/',-1) = #{typeContent3}
ORDER BY submit_time DESC

SELECT `data`.id, content, file_name as fileName, src, data_class.id as dataClassId, data_class.name as dataClassName, role.id as dataClassRoleId, role.name as dataClassRoleName, user1.username as dataClassUserUsername, user1.password as dataClassUserPassword, user1.id_card as dataClassUserIdCard, user1.first_login as dataClassUserFirstLogin, department.code as dataClassDepartmentCode, department.name as dataClassDepartmentName, data_class.flag as dataClassFlag, type_contents as typeContents, user2.username, user2.password, user2.id_card as idCard, user2.first_login as firstLogin, submit_time as submitTime, delete_time as deleteTime, `data`.flag FROM `data` LEFT JOIN data_class ON `data`.data_class = data_class.id LEFT JOIN role ON data_class.role = role.id LEFT JOIN `user` user1 ON data_class.`user` = user1.username LEFT JOIN department ON data_class.department = department.code LEFT JOIN `user` user2 ON `data`.user = user2.username WHERE data_class.name NOT IN ('私人类型') AND `data`.flag=0 AND (SELECT department FROM user_info WHERE user_info.user = `data`.user) = #{departmentCode} AND (SELECT role FROM user_role WHERE user_role.`user`=`data`.`user`) = #{roleId}
AND `data`.data_class = #{dataClassId}
AND SUBSTRING_INDEX(`data`.type_contents,'/',1) = #{typeContent1}
AND SUBSTRING_INDEX(SUBSTRING_INDEX(`data`.type_contents,'/',2),'/',-1) = #{typeContent2}
AND SUBSTRING_INDEX(`data`.type_contents,'/',-1) = #{typeContent3}
ORDER BY submit_time DESC

SELECT id, content, `action`, `time`, user1.username as operationUserUsername, user1.password as operationUserPassword, user1.id_card as operationUserIdCard, user1.first_login as operationUserFirstLogin, operated_id as operatedId, operated_type as operatedType, description FROM log LEFT JOIN `user` as user1 ON user1.username =operation_user WHERE 1=1
AND (operation_user = #{username} OR operated_id in(SELECT id FROM `data` WHERE `data`.user = #{username}))
AND (SELECT data_class FROM `data` WHERE `data`.id = log.operated_id) = #{dataClassId}
AND log.`action` IN ${action}
ORDER BY `time` desc

SELECT id, class_type.`name`, department.code as departmentCode, department.name as departmentName, `user`.username as username, `user`.password as password, `user`.id_card as idCard, `user`.first_login as firstLogin FROM class_type LEFT JOIN department ON department.code = class_type.department LEFT JOIN `user` ON `user`.username = class_type.`user` WHERE 1=1
AND department.code = #{departmentCode}
AND class_type.id = #{classTypeId};

SELECT type_content.id, type_content.`name`, class_type.id as classTypeId, class_type.name as classTypeName, department.code as departmentCode, department.name as departmentName, classTypeUser.username as classTypeUsername, classTypeUser.password as classTypePassword, classTypeUser.id_card as classTypeIdCard, classTypeUser.first_login as classTypeFirstLogin, typeContentUser.username as typeContentUsername, typeContentUser.password as typeContentPassword, typeContentUser.id_card as typeContentIdCard, typeContentUser.first_login as typeContentFirstLogin FROM type_content LEFT JOIN class_type ON class_type.id = type_content.class_type LEFT JOIN department ON department.code = class_type.department LEFT JOIN `user` as classTypeUser ON classTypeUser.username = class_type.user LEFT JOIN `user` as typeContentUser ON typeContentUser.username = type_content.user WHERE department.code = #{departmentCode}
AND type_content.class_type = #{classTypeId}
ORDER BY class_type.id

SELECT type_content.id, type_content.`name`, class_type.id as classTypeId, class_type.name as classTypeName, department.code as departmentCode, department.name as departmentName, classTypeUser.username as classTypeUsername, classTypeUser.password as classTypePassword, classTypeUser.id_card as classTypeIdCard, classTypeUser.first_login as classTypeFirstLogin, typeContentUser.username as typeContentUsername, typeContentUser.password as typeContentPassword, typeContentUser.id_card as typeContentIdCard, typeContentUser.first_login as typeContentFirstLogin FROM type_content LEFT JOIN class_type ON class_type.id = type_content.class_type LEFT JOIN department ON department.code = class_type.department LEFT JOIN `user` as classTypeUser ON classTypeUser.username = class_type.user LEFT JOIN `user` as typeContentUser ON typeContentUser.username = type_content.user WHERE 1=1
AND type_content.id = #{typeContentId}
AND type_content.class_type = #{classTypeId}
